<?phpuse Php\PrimeiroProjeto\Router;require __DIR__ . '/vendor/autoload.php';$method = $_SERVER['REQUEST_METHOD'];$path = $_SERVER['PATH_INFO'] ?? '/';$r = new Router($method, $path);$r->get('/olamundo', function () {    return 'Olá mundo!';});$r->get('/olapessoa/{nome}', function ($params){    return "Olá {$params[1]}";});$r->get('/ex1/formulario', function (){    include 'public/ex1.html';});$r->post('/ex1/resposta', function (){    $valor1 = filter_input(INPUT_POST, 'valor1');    $valor2 = filter_input(INPUT_POST, 'valor2');    $soma = $valor2 + $valor1;    return "A soma é: {$soma}";});$result = $r->handler();if(!$result) {    http_response_code(404);    echo "Página não encontrada!";    die();}echo $result($r->getParams());